import React from "react";
import { useStateValue } from "../../State/StateProvider";

import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";

import "./Checkout.css";
import Subtotal from "../Subtotal/Subtotal";
// import banner from "../../assets/photos/skateboard1.png";

const Checkoout = () => {
  const [{ cart }] = useStateValue();

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
  }));
  return (
    // <div className="checkout">
    //   <img className="checkout__ad" src={banner} alt="" />
    //   <div className="checkout__container">
    //     <div className="checkout__left">
    //       <div style={{ paddingLeft: "10%" }}>
    //         <h2 className="checkout__title checkout__title__left">
    //           Your Shopping Cart
    //           <Subtotal className="checkout__title_right" />
    //         </h2>
    //       </div>
    //     </div>
    //     <div className="checkout__right">
    //       <div><Subtotal className="checkout__title_right" /></div>
    //     </div>
    //   </div>
    // </div>
    <div>
      <Grid
        container
        spacing={{ xs: 1, md: 3 }}
        columns={{ xs: 6, sm: 8, md: 12 }}
      >
        <Grid sx={{ marginTop: "3px" }} item xs={5.5} sm={6} md={6}>
          <Subtotal />
        </Grid>
        <Grid item xs={6} sm={6} md={4}>
          <Item>
            <div>
              <h2>Summary of purchase:</h2>
              <ListItemButton
                style={{ display: "flex", flexDirection: "column" }}
                component="a"
                href="#simple-list"
              >
                {cart.map((item, index) => (
                  <div style={{ display: "flex" }}>
                    <ListItemText
                      style={{ fontWeight: 700 }}
                      key={`${index}_title`}
                      primary={item.title}
                    />
                    <ListItemText
                      key={`${index}_price`}
                      primary={`: $${item.price}`}
                    />
                  </div>
                ))}
                <div style={{ display: "flex" }}>
                  <ListItemText primary="Tax: " />
                  <ListItemText primary="$4.78" />
                </div>
              </ListItemButton>
            </div>
          </Item>
        </Grid>
      </Grid>
    </div>
  );
};

export default Checkoout;
