export const initialState = {
  cart: [JSON.parse(localStorage.shift().getItem("cart"))],
  // user: null,
};

export const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_CART":
      return {
        ...state,
        cart: [...state.cart, action.item],
      };
      case "UPDATE_CART":
        return {
          ...state,
          cart: action.cart,
